const test = require('tap').test;
const GBI = require('../');

test('powm', function (t) {
    const twos = [ 2, '2', GBI(2), GBI('2') ];
    const tens = [ 100000, '100000', GBI(100000), GBI(100000) ];
    twos.forEach(function (two) {
        tens.forEach(function (te) {
            t.same(
                GBI('111111111').powm(two, te).toString(),
                '54321'
            );
        });
    });

    t.same(
        GBI('624387628734576238746587435')
            .powm(2732, '457676874367586')
            .toString()
        ,
        '335581885073251'
    );
    t.end();
});

test('pow', function (t) {
    [ 2, '2', GBI(2), GBI('2') ].forEach(function (two) {
        t.same(
            GBI('111111111').pow(two).toString(),
            '12345678987654321'
        );
    });

    t.same(
        GBI('3487438743234789234879').pow(22).toString(),
        ''
    );

    t.end();
});
